class Company_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get company by id
     */
    function get_company($id)
    {
        return $this->db->get_where('company',array('id'=>$id))->row_array();
    }
        
    /*
     * Get all company
     */
    function get_all_company()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('company')->result_array();
    }
        
    /*
     * function to add new company
     */
    function add_company($params)
    {
        $this->db->insert('company',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update company
     */
    function update_company($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('company',$params);
    }
    
    /*
     * function to delete company
     */
    function delete_company($id)
    {
        return $this->db->delete('company',array('id'=>$id));
    }
}
